/* 聂卫平纪念网站 - 中国风水墨风格 */
:root {
    /* 水墨风格色彩 */
    --ink-black: #1a1a1a;
    --ink-dark: #2d2d2d;
    --ink-gray: #4a4a4a;
    --ink-light: #8c8c8c;
    --ink-white: #f5f5f5;
    --paper-color: #f8f5f0;
    --paper-dark: #e8e5e0;
    --accent-red: #c62828;
    --accent-gold: #d4af37;
    --shadow-soft: rgba(0, 0, 0, 0.1);
    --shadow-medium: rgba(0, 0, 0, 0.2);
    --shadow-strong: rgba(0, 0, 0, 0.3);

    /* 字体 */
    --font-heading: 'Ma Shan Zheng', cursive;
    --font-body: 'Noto Serif SC', serif;
    --font-accent: 'ZCOOL QingKe HuangYou', sans-serif;

    /* 间距 */
    --space-xs: 0.5rem;
    --space-sm: 1rem;
    --space-md: 2rem;
    --space-lg: 3rem;
    --space-xl: 5rem;

    /* 圆角 */
    --radius-sm: 4px;
    --radius-md: 8px;
    --radius-lg: 16px;
    --radius-full: 9999px;

    /* 过渡 */
    --transition-fast: 0.2s ease;
    --transition-normal: 0.3s ease;
    --transition-slow: 0.5s ease;
}

/* 基础重置 */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    scroll-behavior: smooth;
    font-size: 16px;
}

body {
    font-family: var(--font-body);
    color: var(--ink-black);
    background-color: var(--paper-color);
    line-height: 1.6;
    overflow-x: hidden;
    position: relative;
}

/* 水墨背景效果 */
.ink-background {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: 
        radial-gradient(circle at 20% 80%, rgba(198, 40, 40, 0.05) 0%, transparent 50%),
        radial-gradient(circle at 80% 20%, rgba(42, 42, 42, 0.05) 0%, transparent 50%);
    z-index: -2;
    pointer-events: none;
}

.ink-splatter {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: 
        url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="100" height="100" opacity="0.02"><path d="M20,30 Q40,10 60,30 T100,30" stroke="black" fill="none" stroke-width="2"/><circle cx="30" cy="70" r="8" fill="black"/><circle cx="70" cy="20" r="5" fill="black"/></svg>');
    z-index: -1;
    pointer-events: none;
    opacity: 0.1;
}

/* 导航栏 */
.main-nav {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    background-color: rgba(248, 245, 240, 0.95);
    backdrop-filter: blur(10px);
    box-shadow: 0 2px 20px var(--shadow-soft);
    z-index: 1000;
    padding: var(--space-sm) 0;
    border-bottom: 1px solid var(--paper-dark);
}

.nav-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 var(--space-md);
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.nav-logo {
    display: flex;
    align-items: center;
    gap: var(--space-sm);
}

.nav-logo h1 {
    font-family: var(--font-heading);
    font-size: 1.5rem;
    color: var(--ink-black);
    font-weight: normal;
    letter-spacing: 2px;
}

.go-stone {
    width: 24px;
    height: 24px;
    border-radius: 50%;
    display: inline-block;
    position: relative;
    box-shadow: 0 2px 8px var(--shadow-medium);
}

.go-stone.black {
    background: linear-gradient(135deg, #1a1a1a, #333);
}

.go-stone.white {
    background: linear-gradient(135deg, #f5f5f5, #ddd);
    border: 1px solid var(--ink-light);
}

.nav-menu {
    display: flex;
    list-style: none;
    gap: var(--space-lg);
}

.nav-link {
    text-decoration: none;
    color: var(--ink-gray);
    font-size: 1.1rem;
    position: relative;
    padding: var(--space-xs) 0;
    transition: color var(--transition-fast);
}

.nav-link::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 0;
    height: 2px;
    background-color: var(--accent-red);
    transition: width var(--transition-normal);
}

.nav-link:hover {
    color: var(--ink-black);
}

.nav-link:hover::after {
    width: 100%;
}

.nav-toggle {
    display: none;
    background: none;
    border: none;
    cursor: pointer;
    padding: var(--space-xs);
}

.bar {
    display: block;
    width: 25px;
    height: 2px;
    background-color: var(--ink-black);
    margin: 5px 0;
    transition: var(--transition-normal);
}

/* 英雄区域 */
.hero {
    min-height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding: var(--space-xl) var(--space-md);
    position: relative;
    margin-top: 80px;
}

.hero-content {
    max-width: 1200px;
    margin: 0 auto;
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--space-xl);
    align-items: center;
}

.hero-text {
    animation: fadeInUp 1s ease;
}

.hero-title {
    font-family: var(--font-heading);
    font-size: 4rem;
    line-height: 1.2;
    margin-bottom: var(--space-sm);
    letter-spacing: 3px;
}

.highlight {
    color: var(--accent-red);
    position: relative;
    display: inline-block;
}

.highlight::after {
    content: '';
    position: absolute;
    bottom: 5px;
    left: 0;
    width: 100%;
    height: 8px;
    background-color: var(--accent-red);
    opacity: 0.2;
    z-index: -1;
}

.hero-subtitle {
    font-family: var(--font-accent);
    font-size: 2rem;
    color: var(--ink-gray);
    margin-bottom: var(--space-md);
    letter-spacing: 5px;
}

.hero-quote {
    font-size: 1.5rem;
    font-style: italic;
    color: var(--ink-dark);
    margin-bottom: var(--space-lg);
    padding-left: var(--space-md);
    border-left: 3px solid var(--accent-gold);
}

.hero-buttons {
    display: flex;
    gap: var(--space-md);
    flex-wrap: wrap;
}

.btn {
    display: inline-block;
    padding: var(--space-sm) var(--space-lg);
    text-decoration: none;
    border-radius: var(--radius-full);
    font-size: 1.1rem;
    font-weight: bold;
    transition: all var(--transition-normal);
    cursor: pointer;
    border: 2px solid transparent;
}

.btn-primary {
    background-color: var(--accent-red);
    color: var(--ink-white);
}

.btn-primary:hover {
    background-color: #a71c1c;
    transform: translateY(-2px);
    box-shadow: 0 8px 20px rgba(198, 40, 40, 0.3);
}

.btn-outline {
    background-color: transparent;
    color: var(--ink-black);
    border-color: var(--ink-black);
}

.btn-outline:hover {
    background-color: var(--ink-black);
    color: var(--ink-white);
    transform: translateY(-2px);
}

.hero-image {
    animation: fadeInRight 1s ease;
}

.portrait-frame {
    position: relative;
    width: 100%;
    max-width: 400px;
    margin: 0 auto;
}

.portrait {
    width: 100%;
    height: auto;
    border-radius: var(--radius-md);
    box-shadow: 0 20px 40px var(--shadow-strong);
    filter: grayscale(30%) contrast(110%);
    object-fit: cover;
    aspect-ratio: 428/643;
}

.frame-decoration {
    position: absolute;
    top: -10px;
    left: -10px;
    right: -10px;
    bottom: -10px;
    border: 2px solid var(--accent-gold);
    border-radius: var(--radius-md);
    z-index: -1;
    opacity: 0.5;
}

.scroll-indicator {
    position: absolute;
    bottom: var(--space-xl);
    left: 50%;
    transform: translateX(-50%);
    display: flex;
    flex-direction: column;
    align-items: center;
    animation: bounce 2s infinite;
}

.scroll-text {
    font-size: 0.9rem;
    color: var(--ink-light);
    margin-bottom: var(--space-xs);
    letter-spacing: 2px;
}

.scroll-line {
    width: 1px;
    height: 40px;
    background: linear-gradient(to bottom, var(--ink-light), transparent);
}

/* 通用部分样式 */
.section {
    padding: var(--space-xl) var(--space-md);
    max-width: 1200px;
    margin: 0 auto;
}

.section-header {
    text-align: center;
    margin-bottom: var(--space-xl);
}

.section-title {
    font-family: var(--font-heading);
    font-size: 3rem;
    margin-bottom: var(--space-sm);
    position: relative;
    display: inline-block;
}

.section-title::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 10%;
    width: 80%;
    height: 3px;
    background: linear-gradient(to right, transparent, var(--accent-red), transparent);
}

.section-subtitle {
    font-family: var(--font-accent);
    font-size: 1.5rem;
    color: var(--ink-gray);
    margin-bottom: var(--space-md);
    letter-spacing: 3px;
}

.section-divider {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: var(--space-md);
}

.divider-line {
    width: 100px;
    height: 1px;
    background-color: var(--ink-light);
}

.divider-icon {
    font-size: 1.5rem;
    opacity: 0.5;
}

/* 时间线 */
.timeline {
    position: relative;
    max-width: 800px;
    margin: 0 auto;
}

.timeline::before {
    content: '';
    position: absolute;
    top: 0;
    bottom: 0;
    left: 50%;
    width: 2px;
    background: linear-gradient(to bottom, transparent, var(--ink-light), transparent);
    transform: translateX(-50%);
}

.timeline-item {
    display: flex;
    margin-bottom: var(--space-xl);
    position: relative;
}

.timeline-item:nth-child(odd) {
    flex-direction: row;
}

.timeline-item:nth-child(even) {
    flex-direction: row-reverse;
}

.timeline-date {
    flex: 1;
    text-align: center;
    font-family: var(--font-accent);
    font-size: 1.8rem;
    font-weight: bold;
    color: var(--accent-red);
    padding: var(--space-sm);
    position: relative;
    z-index: 1;
}

.timeline-content {
    flex: 2;
    padding: var(--space-md);
    background-color: var(--ink-white);
    border-radius: var(--radius-md);
    box-shadow: 0 5px 15px var(--shadow-soft);
    position: relative;
}

.timeline-content h3 {
    font-family: var(--font-heading);
    font-size: 1.8rem;
    margin-bottom: var(--space-sm);
    color: var(--ink-black);
}

.timeline-marker {
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    display: flex;
    flex-direction: column;
    align-items: center;
    z-index: 2;
}

.marker-circle {
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background-color: var(--accent-red);
    border: 3px solid var(--ink-white);
    box-shadow: 0 0 10px var(--shadow-medium);
}

.marker-line {
    width: 2px;
    height: 100%;
    background-color: var(--accent-red);
    opacity: 0.3;
}

/* 成就卡片 */
.achievements-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: var(--space-lg);
    margin-bottom: var(--space-xl);
}

.achievement-card {
    background-color: var(--ink-white);
    padding: var(--space-lg);
    border-radius: var(--radius-md);
    box-shadow: 0 5px 15px var(--shadow-soft);
    text-align: center;
    transition: transform var(--transition-normal), box-shadow var(--transition-normal);
    border-top: 4px solid var(--accent-red);
}

.achievement-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 30px var(--shadow-medium);
}

.achievement-icon {
    font-size: 3rem;
    color: var(--accent-red);
    margin-bottom: var(--space-md);
}

.achievement-card h3 {
    font-family: var(--font-heading);
    font-size: 1.8rem;
    margin-bottom: var(--space-sm);
    color: var(--ink-black);
}

/* 统计数据 */
.stats-container {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
    gap: var(--space-lg);
    text-align: center;
}

.stat-item {
    padding: var(--space-md);
}

.stat-number {
    font-family: var(--font-accent);
    font-size: 4rem;
    font-weight: bold;
    color: var(--accent-red);
    margin-bottom: var(--space-xs);
}

.stat-label {
    font-size: 1.2rem;
    color: var(--ink-gray);
    letter-spacing: 1px;
}

/* 围棋棋盘 */
.go-board-container {
    max-width: 800px;
    margin: 0 auto var(--space-xl);
}

.go-board {
    position: relative;
    width: 100%;
    aspect-ratio: 1;
    background-color: #e6cda2;
    border-radius: var(--radius-md);
    box-shadow: 0 20px 40px var(--shadow-strong);
    padding: 40px;
}

.board-grid {
    position: absolute;
    top: 40px;
    left: 40px;
    right: 40px;
    bottom: 40px;
    background-image: 
        linear-gradient(to right, #8b4513 1px, transparent 1px),
        linear-gradient(to bottom, #8b4513 1px, transparent 1px);
    background-size: calc(100% / 18) calc(100% / 18);
    background-position: center center;
}

.board-stones {
    position: absolute;
    top: 40px;
    left: 40px;
    right: 40px;
    bottom: 40px;
}

.board-info {
    position: absolute;
    bottom: -60px;
    left: 0;
    right: 0;
    text-align: center;
    padding: var(--space-md);
    background-color: var(--ink-white);
    border-radius: var(--radius-md);
    box-shadow: 0 5px 15px var(--shadow-soft);
}

.board-info h3 {
    font-family: var(--font-heading);
    font-size: 1.8rem;
    margin-bottom: var(--space-sm);
    color: var(--ink-black);
}

.game-quotes {
    max-width: 800px;
    margin: 0 auto;
}

.game-quote {
    font-size: 1.5rem;
    font-style: italic;
    color: var(--ink-dark);
    padding: var(--space-lg);
    background-color: var(--ink-white);
    border-radius: var(--radius-md);
    box-shadow: 0 5px 15px var(--shadow-soft);
    border-left: 5px solid var(--accent-gold);
}

.game-quote footer {
    margin-top: var(--space-sm);
    text-align: right;
    font-size: 1.2rem;
    color: var(--ink-gray);
    font-style: normal;
}

/* 传承部分 */
.legacy-content {
    display: grid;
    grid-template-columns: 2fr 1fr;
    gap: var(--space-xl);
    align-items: center;
}

.legacy-text h3 {
    font-family: var(--font-heading);
    font-size: 2.5rem;
    margin-bottom: var(--space-md);
    color: var(--ink-black);
}

.legacy-text p {
    margin-bottom: var(--space-md);
    font-size: 1.2rem;
    line-height: 1.8;
}

.ink-painting {
    position: relative;
    width: 100%;
    height: 400px;
    background-color: var(--ink-white);
    border-radius: var(--radius-md);
    box-shadow: 0 10px 30px var(--shadow-medium);
    overflow: hidden;
}

.painting-stroke {
    position: absolute;
    background-color: var(--ink-black);
    border-radius: var(--radius-full);
}

.painting-stroke:nth-child(1) {
    top: 20%;
    left: 30%;
    width: 60%;
    height: 3px;
    transform: rotate(15deg);
    opacity: 0.8;
}

.painting-stroke:nth-child(2) {
    top: 40%;
    left: 20%;
    width: 40%;
    height: 5px;
    transform: rotate(-10deg);
    opacity: 0.6;
}

.painting-stroke:nth-child(3) {
    top: 60%;
    left: 40%;
    width: 30%;
    height: 4px;
    transform: rotate(5deg);
    opacity: 0.7;
}

.painting-text {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    font-family: var(--font-heading);
    font-size: 3rem;
    color: var(--ink-black);
    opacity: 0.3;
    letter-spacing: 10px;
}

/* 页脚 */
.main-footer {
    background-color: var(--ink-dark);
    color: var(--ink-white);
    padding: var(--space-xl) var(--space-md);
    margin-top: var(--space-xl);
}

.footer-content {
    max-width: 1200px;
    margin: 0 auto;
    text-align: center;
}

.footer-logo {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: var(--space-sm);
    margin-bottom: var(--space-lg);
    font-family: var(--font-heading);
    font-size: 2rem;
    letter-spacing: 3px;
}

.footer-quote {
    font-size: 1.5rem;
    font-style: italic;
    margin-bottom: var(--space-lg);
    color: var(--ink-light);
    max-width: 800px;
    margin-left: auto;
    margin-right: auto;
}

.footer-links {
    display: flex;
    justify-content: center;
    gap: var(--space-lg);
    margin-bottom: var(--space-lg);
    flex-wrap: wrap;
}

.footer-links a {
    color: var(--ink-light);
    text-decoration: none;
    font-size: 1.1rem;
    transition: color var(--transition-fast);
}

.footer-links a:hover {
    color: var(--accent-gold);
}

.footer-copyright {
    border-top: 1px solid var(--ink-gray);
    padding-top: var(--space-md);
    color: var(--ink-light);
}

.deerflow-badge {
    display: inline-block;
    margin-top: var(--space-sm);
    padding: var(--space-xs) var(--space-sm);
    background-color: rgba(255, 255, 255, 0.1);
    color: var(--ink-light);
    text-decoration: none;
    border-radius: var(--radius-sm);
    font-size: 0.9rem;
    transition: all var(--transition-fast);
}

.deerflow-badge:hover {
    background-color: rgba(255, 255, 255, 0.2);
    color: var(--ink-white);
}

/* 返回顶部按钮 */
.back-to-top {
    position: fixed;
    bottom: var(--space-lg);
    right: var(--space-lg);
    width: 50px;
    height: 50px;
    background-color: var(--accent-red);
    color: var(--ink-white);
    border: none;
    border-radius: 50%;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    box-shadow: 0 5px 15px var(--shadow-medium);
    transition: all var(--transition-normal);
    opacity: 0;
    visibility: hidden;
    z-index: 999;
}

.back-to-top.visible {
    opacity: 1;
    visibility: visible;
}

.back-to-top:hover {
    background-color: #a71c1c;
    transform: translateY(-5px);
    box-shadow: 0 10px 20px rgba(198, 40, 40, 0.4);
}

/* 浮动棋子 */
.floating-stones {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: -1;
}

.floating-stone {
    position: absolute;
    border-radius: 50%;
    opacity: 0.1;
    animation: float 20s infinite linear;
}

.floating-stone.black {
    background-color: var(--ink-black);
}

.floating-stone.white {
    background-color: var(--ink-white);
    border: 1px solid var(--ink-light);
}

.floating-stone:nth-child(1) {
    width: 40px;
    height: 40px;
    top: 10%;
    left: 10%;
    animation-delay: 0s;
}

.floating-stone:nth-child(2) {
    width: 60px;
    height: 60px;
    top: 60%;
    left: 80%;
    animation-delay: -5s;
    animation-duration: 25s;
}

.floating-stone:nth-child(3) {
    width: 30px;
    height: 30px;
    top: 80%;
    left: 20%;
    animation-delay: -10s;
    animation-duration: 30s;
}

.floating-stone:nth-child(4) {
    width: 50px;
    height: 50px;
    top: 30%;
    left: 70%;
    animation-delay: -15s;
    animation-duration: 35s;
}

/* 动画 */
@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes fadeInRight {
    from {
        opacity: 0;
        transform: translateX(30px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes bounce {
    0%, 20%, 50%, 80%, 100% {
        transform: translateX(-50%) translateY(0);
    }
    40% {
        transform: translateX(-50%) translateY(-10px);
    }
    60% {
        transform: translateX(-50%) translateY(-5px);
    }
}

@keyframes float {
    0% {
        transform: translateY(0) rotate(0deg);
    }
    25% {
        transform: translateY(-20px) rotate(90deg);
    }
    50% {
        transform: translateY(0) rotate(180deg);
    }
    75% {
        transform: translateY(20px) rotate(270deg);
    }
    100% {
        transform: translateY(0) rotate(360deg);
    }
}

/* 响应式设计 */
@media (max-width: 1024px) {
    .hero-content {
        grid-template-columns: 1fr;
        gap: var(--space-lg);
    }

    .hero-title {
        font-size: 3rem;
    }

    .hero-subtitle {
        font-size: 1.8rem;
    }

    .section-title {
        font-size: 2.5rem;
    }
}

@media (max-width: 768px) {
    .nav-menu {
        position: fixed;
        top: 80px;
        left: 0;
        width: 100%;
        background-color: var(--ink-white);
        flex-direction: column;
        align-items: center;
        padding: var(--space-lg);
        gap: var(--space-md);
        box-shadow: 0 10px 20px var(--shadow-medium);
        transform: translateY(-100%);
        opacity: 0;
        transition: all var(--transition-normal);
        z-index: 999;
    }

    .nav-menu.active {
        transform: translateY(0);
        opacity: 1;
    }

    .nav-toggle {
        display: block;
    }

    .nav-toggle.active .bar:nth-child(1) {
        transform: rotate(45deg) translate(5px, 5px);
    }

    .nav-toggle.active .bar:nth-child(2) {
        opacity: 0;
    }

    .nav-toggle.active .bar:nth-child(3) {
        transform: rotate(-45deg) translate(7px, -6px);
    }

    .timeline::before {
        left: 30px;
    }

    .timeline-item {
        flex-direction: row !important;
        margin-left: 60px;
    }

    .timeline-date {
        position: absolute;
        left: -60px;
        top: 0;
        text-align: left;
        width: 50px;
    }

    .timeline-marker {
        left: 30px;
    }

    .legacy-content {
        grid-template-columns: 1fr;
    }

    .hero-title {
        font-size: 2.5rem;
    }

    .hero-subtitle {
        font-size: 1.5rem;
    }

    .hero-quote {
        font-size: 1.3rem;
    }
}

@media (max-width: 480px) {
    .hero-buttons {
        flex-direction: column;
        gap: var(--space-sm);
    }

    .btn {
        width: 100%;
        text-align: center;
    }

    .achievements-grid {
        grid-template-columns: 1fr;
    }

    .stats-container {
        grid-template-columns: repeat(2, 1fr);
    }

    .section-title {
        font-size: 2rem;
    }

    .section-subtitle {
        font-size: 1.2rem;
    }
}

/* 打印样式 */
@media print {
    .main-nav,
    .back-to-top,
    .floating-stones,
    .scroll-indicator {
        display: none;
    }

    body {
        background-color: white;
        color: black;
    }

    .section {
        page-break-inside: avoid;
    }

    .hero {
        min-height: auto;
        margin-top: 0;
    }
}

// 聂卫平纪念网站 - 交互效果

document.addEventListener('DOMContentLoaded', function() {
    // 初始化
    initNavigation();
    initScrollEffects();
    initStatsCounter();
    initGoBoard();
    initBackToTop();
    initAnimations();

    console.log('棋圣聂卫平纪念网站已加载 - 永恒的围棋传奇');
});

// 导航菜单功能
function initNavigation() {
    const navToggle = document.querySelector('.nav-toggle');
    const navMenu = document.querySelector('.nav-menu');
    const navLinks = document.querySelectorAll('.nav-link');

    // 切换移动端菜单
    navToggle.addEventListener('click', function() {
        navMenu.classList.toggle('active');
        navToggle.classList.toggle('active');
    });

    // 点击导航链接时关闭菜单
    navLinks.forEach(link => {
        link.addEventListener('click', function() {
            navMenu.classList.remove('active');
            navToggle.classList.remove('active');
        });
    });

    // 滚动时高亮当前部分
    window.addEventListener('scroll', highlightCurrentSection);
}

// 高亮当前滚动到的部分
function highlightCurrentSection() {
    const sections = document.querySelectorAll('section');
    const navLinks = document.querySelectorAll('.nav-link');

    let currentSection = '';

    sections.forEach(section => {
        const sectionTop = section.offsetTop - 100;
        const sectionHeight = section.clientHeight;
        const scrollPosition = window.scrollY;

        if (scrollPosition >= sectionTop && scrollPosition < sectionTop + sectionHeight) {
            currentSection = section.getAttribute('id');
        }
    });

    navLinks.forEach(link => {
        link.classList.remove('active');
        if (link.getAttribute('href') === `#${currentSection}`) {
            link.classList.add('active');
        }
    });
}

// 滚动效果
function initScrollEffects() {
    // 添加滚动时的淡入效果
    const observerOptions = {
        threshold: 0.1,
        rootMargin: '0px 0px -50px 0px'
    };

    const observer = new IntersectionObserver(function(entries) {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                entry.target.classList.add('animated');
            }
        });
    }, observerOptions);

    // 观察需要动画的元素
    const animatedElements = document.querySelectorAll('.timeline-item, .achievement-card, .game-quote, .legacy-text, .legacy-image');
    animatedElements.forEach(el => observer.observe(el));

    // 平滑滚动到锚点
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function(e) {
            const targetId = this.getAttribute('href');
            if (targetId === '#') return;

            const targetElement = document.querySelector(targetId);
            if (targetElement) {
                e.preventDefault();
                window.scrollTo({
                    top: targetElement.offsetTop - 80,
                    behavior: 'smooth'
                });
            }
        });
    });
}

// 统计数据计数器
function initStatsCounter() {
    const statNumbers = document.querySelectorAll('.stat-number');

    const observerOptions = {
        threshold: 0.5
    };

    const observer = new IntersectionObserver(function(entries) {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                const statNumber = entry.target;
                const target = parseInt(statNumber.getAttribute('data-count'));
                const duration = 2000; // 2秒
                const increment = target / (duration / 16); // 60fps
                let current = 0;

                const timer = setInterval(() => {
                    current += increment;
                    if (current >= target) {
                        current = target;
                        clearInterval(timer);
                    }
                    statNumber.textContent = Math.floor(current);
                }, 16);

                observer.unobserve(statNumber);
            }
        });
    }, observerOptions);

    statNumbers.forEach(number => observer.observe(number));
}

// 围棋棋盘初始化
function initGoBoard() {
    const boardStones = document.querySelector('.board-stones');
    if (!boardStones) return;

    // 经典棋局棋子位置 (模拟1985年决胜局)
    const stonePositions = [
        { type: 'black', x: 4, y: 4 },
        { type: 'white', x: 4, y: 16 },
        { type: 'black', x: 16, y: 4 },
        { type: 'white', x: 16, y: 16 },
        { type: 'black', x: 10, y: 10 },
        { type: 'white', x: 9, y: 9 },
        { type: 'black', x: 3, y: 15 },
        { type: 'white', x: 15, y: 3 },
        { type: 'black', x: 17, y: 17 },
        { type: 'white', x: 2, y: 2 }
    ];

    // 创建棋子
    stonePositions.forEach((stone, index) => {
        const stoneElement = document.createElement('div');
        stoneElement.className = `board-stone ${stone.type}`;

        // 计算位置 (19x19棋盘)
        const xPercent = (stone.x / 18) * 100;
        const yPercent = (stone.y / 18) * 100;

        stoneElement.style.left = `${xPercent}%`;
        stoneElement.style.top = `${yPercent}%`;
        stoneElement.style.animationDelay = `${index * 0.2}s`;

        boardStones.appendChild(stoneElement);
    });

    // 添加棋盘样式
    const style = document.createElement('style');
    style.textContent = `
        .board-stone {
            position: absolute;
            width: 4%;
            height: 4%;
            border-radius: 50%;
            transform: translate(-50%, -50%);
            box-shadow: 0 2px 5px rgba(0,0,0,0.3);
            animation: stoneAppear 0.5s ease-out forwards;
            opacity: 0;
        }

        .board-stone.black {
            background: radial-gradient(circle at 30% 30%, #555, #000);
        }

        .board-stone.white {
            background: radial-gradient(circle at 30% 30%, #fff, #ddd);
            border: 1px solid #aaa;
        }

        @keyframes stoneAppear {
            from {
                opacity: 0;
                transform: translate(-50%, -50%) scale(0);
            }
            to {
                opacity: 1;
                transform: translate(-50%, -50%) scale(1);
            }
        }
    `;

    document.head.appendChild(style);
}

// 返回顶部按钮
function initBackToTop() {
    const backToTopBtn = document.querySelector('.back-to-top');

    window.addEventListener('scroll', function() {
        if (window.scrollY > 300) {
            backToTopBtn.classList.add('visible');
        } else {
            backToTopBtn.classList.remove('visible');
        }
    });

    backToTopBtn.addEventListener('click', function() {
        window.scrollTo({
            top: 0,
            behavior: 'smooth'
        });
    });
}

// 初始化动画
function initAnimations() {
    // 添加滚动时的水墨效果
    let lastScrollTop = 0;
    const inkSplatter = document.querySelector('.ink-splatter');

    window.addEventListener('scroll', function() {
        const scrollTop = window.scrollY;
        const scrollDirection = scrollTop > lastScrollTop ? 'down' : 'up';

        // 根据滚动方向调整水墨效果
        if (inkSplatter) {
            const opacity = 0.1 + (scrollTop / 5000);
            inkSplatter.style.opacity = Math.min(opacity, 0.3);

            // 轻微移动效果
            const moveX = (scrollTop % 100) / 100;
            inkSplatter.style.transform = `translateX(${moveX}px)`;
        }

        lastScrollTop = scrollTop;
    });

    // 鼠标移动时的墨水效果
    document.addEventListener('mousemove', function(e) {
        const floatingStones = document.querySelectorAll('.floating-stone');

        floatingStones.forEach((stone, index) => {
            const speed = 0.01 + (index * 0.005);
            const x = (window.innerWidth - e.clientX) * speed;
            const y = (window.innerHeight - e.clientY) * speed;

            stone.style.transform = `translate(${x}px, ${y}px)`;
        });
    });

    // 页面加载时的动画序列
    setTimeout(() => {
        document.body.classList.add('loaded');
    }, 100);
}

// 添加键盘快捷键
document.addEventListener('keydown', function(e) {
    // 空格键滚动
    if (e.code === 'Space' && !e.target.matches('input, textarea')) {
        e.preventDefault();
        window.scrollBy({
            top: window.innerHeight * 0.8,
            behavior: 'smooth'
        });
    }

    // ESC键返回顶部
    if (e.code === 'Escape') {
        window.scrollTo({
            top: 0,
            behavior: 'smooth'
        });
    }

    // 数字键跳转到对应部分
    if (e.code >= 'Digit1' && e.code <= 'Digit5') {
        const sectionIndex = parseInt(e.code.replace('Digit', '')) - 1;
        const sections = ['home', 'life', 'achievements', 'gallery', 'legacy'];

        if (sectionIndex < sections.length) {
            const targetSection = document.getElementById(sections[sectionIndex]);
            if (targetSection) {
                window.scrollTo({
                    top: targetSection.offsetTop - 80,
                    behavior: 'smooth'
                });
            }
        }
    }
});

// 添加打印友好功能
window.addEventListener('beforeprint', function() {
    document.body.classList.add('printing');
});

window.addEventListener('afterprint', function() {
    document.body.classList.remove('printing');
});

// 性能优化：图片懒加载
if ('IntersectionObserver' in window) {
    const imageObserver = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                const img = entry.target;
                if (img.dataset.src) {
                    img.src = img.dataset.src;
                    img.removeAttribute('data-src');
                }
                imageObserver.unobserve(img);
            }
        });
    });

    document.querySelectorAll('img[data-src]').forEach(img => imageObserver.observe(img));
}

// 添加触摸设备优化
if ('ontouchstart' in window) {
    document.body.classList.add('touch-device');

    // 为触摸设备调整悬停效果
    const style = document.createElement('style');
    style.textContent = `
        .touch-device .achievement-card:hover {
            transform: none;
        }

        .touch-device .btn:hover {
            transform: none;
        }
    `;
    document.head.appendChild(style);
}

// 添加页面可见性API支持
document.addEventListener('visibilitychange', function() {
    if (document.hidden) {
        console.log('页面隐藏中...');
    } else {
        console.log('页面恢复显示');
    }
});

// 错误处理
window.addEventListener('error', function(e) {
    console.error('页面错误:', e.message);
});

// 页面卸载前的确认
window.addEventListener('beforeunload', function(e) {
    // 可以在这里添加保存功能
});

