/* 蜡烛纪念部分 */
.candle-section {
    background-color: rgba(248, 245, 240, 0.7);
    border-radius: var(--radius-lg);
    margin: var(--space-xl) auto;
    box-shadow: 0 10px 30px var(--shadow-soft);
}

.candle-container {
    max-width: 1000px;
    margin: 0 auto;
    padding: var(--space-lg);
}

.candle-instructions {
    text-align: center;
    margin-bottom: var(--space-lg);
    padding: var(--space-md);
    background-color: var(--ink-white);
    border-radius: var(--radius-md);
    box-shadow: 0 5px 15px var(--shadow-soft);
}

.candle-instructions p {
    font-size: 1.3rem;
    color: var(--ink-dark);
    margin-bottom: var(--space-md);
}

.candle-stats {
    display: flex;
    justify-content: space-around;
    align-items: center;
    flex-wrap: wrap;
    gap: var(--space-md);
    margin-top: var(--space-md);
}

.candle-count {
    display: flex;
    flex-direction: column;
    align-items: center;
}

.count-number {
    font-family: var(--font-accent);
    font-size: 3rem;
    font-weight: bold;
    color: var(--accent-red);
    line-height: 1;
}

.count-label {
    font-size: 1.1rem;
    color: var(--ink-gray);
    margin-top: var(--space-xs);
}

.candle-message {
    padding: var(--space-sm) var(--space-md);
    background-color: rgba(212, 175, 55, 0.1);
    border-radius: var(--radius-md);
    border-left: 3px solid var(--accent-gold);
}

.message-text {
    font-size: 1.2rem;
    color: var(--ink-dark);
    font-style: italic;
}

.candle-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(80px, 1fr));
    gap: var(--space-md);
    margin: var(--space-lg) 0;
    padding: var(--space-md);
    background-color: var(--paper-dark);
    border-radius: var(--radius-md);
    min-height: 300px;
    align-items: end;
}

.candle-item {
    display: flex;
    flex-direction: column;
    align-items: center;
    cursor: pointer;
    transition: transform var(--transition-normal);
    position: relative;
}

.candle-item:hover {
    transform: translateY(-5px);
}

.candle-body {
    width: 20px;
    height: 80px;
    background: linear-gradient(to bottom, #f5f5f5, #e8e5e0, #d4af37);
    border-radius: 10px 10px 4px 4px;
    position: relative;
    box-shadow: inset 0 -5px 10px rgba(0, 0, 0, 0.1);
}

.candle-body::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 20px;
    background: linear-gradient(to bottom, #fff, #f5f5f5);
    border-radius: 10px 10px 0 0;
}

.candle-body::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 10px;
    background-color: #8b4513;
    border-radius: 0 0 4px 4px;
}

.candle-flame {
    width: 15px;
    height: 30px;
    position: absolute;
    top: -35px;
    left: 50%;
    transform: translateX(-50%);
    opacity: 0;
    transition: opacity var(--transition-normal);
}

.candle-lit .candle-flame {
    opacity: 1;
}

.flame-core {
    position: absolute;
    top: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 8px;
    height: 25px;
    background: linear-gradient(to bottom, #ffeb3b, #ff9800);
    border-radius: 50% 50% 20% 20%;
    filter: blur(1px);
    animation: flameFlicker 0.5s infinite alternate;
}

.flame-outer {
    position: absolute;
    top: -5px;
    left: 50%;
    transform: translateX(-50%);
    width: 12px;
    height: 20px;
    background: linear-gradient(to bottom, rgba(255, 193, 7, 0.7), rgba(255, 87, 34, 0.3));
    border-radius: 50% 50% 20% 20%;
    filter: blur(3px);
    animation: flameFlicker 0.3s infinite alternate-reverse;
}

.flame-spark {
    position: absolute;
    top: -10px;
    width: 3px;
    height: 3px;
    background-color: #ffeb3b;
    border-radius: 50%;
    filter: blur(1px);
    animation: sparkFloat 2s infinite;
}

.flame-spark:nth-child(1) {
    left: 30%;
    animation-delay: 0s;
}

.flame-spark:nth-child(2) {
    left: 50%;
    animation-delay: 0.5s;
}

.flame-spark:nth-child(3) {
    left: 70%;
    animation-delay: 1s;
}

.candle-controls {
    display: flex;
    justify-content: center;
    gap: var(--space-md);
    margin: var(--space-lg) 0;
    flex-wrap: wrap;
}

.candle-controls .btn {
    display: flex;
    align-items: center;
    gap: var(--space-xs);
    padding: var(--space-sm) var(--space-lg);
}

.candle-quote {
    margin-top: var(--space-lg);
    padding: var(--space-md);
    text-align: center;
    background-color: var(--ink-white);
    border-radius: var(--radius-md);
    box-shadow: 0 5px 15px var(--shadow-soft);
}

.candle-quote blockquote {
    font-size: 1.4rem;
    font-style: italic;
    color: var(--ink-dark);
    margin: 0;
    padding: 0;
    border-left: none;
}

.candle-quote footer {
    margin-top: var(--space-sm);
    color: var(--ink-gray);
    font-size: 1.1rem;
}

/* 蜡烛动画 */
@keyframes flameFlicker {
    0% {
        transform: translateX(-50%) scale(1, 1);
    }
    50% {
        transform: translateX(-50%) scale(1.1, 0.9);
    }
    100% {
        transform: translateX(-50%) scale(0.9, 1.1);
    }
}

@keyframes sparkFloat {
    0% {
        transform: translateY(0) scale(1);
        opacity: 1;
    }
    50% {
        opacity: 0.7;
    }
    100% {
        transform: translateY(-20px) scale(0.5);
        opacity: 0;
    }
}

@keyframes candleLightUp {
    0% {
        transform: scale(0.8);
        opacity: 0;
    }
    50% {
        transform: scale(1.1);
    }
    100% {
        transform: scale(1);
        opacity: 1;
    }
}

/* 传承部分 */
.legacy-content {
    display: grid;
    grid-template-columns: 2fr 1fr;
    gap: var(--space-xl);
    align-items: center;
}

