@media (max-width: 480px) {

args.old_str:
.candle-item:hover {
    transform: translateY(-5px);
}

.candle-body {
    width: 20px;
    height: 80px;
    background: linear-gradient(to bottom, #f5f5f5, #e8e5e0, #d4af37);
    border-radius: 10px 10px 4px 4px;
    position: relative;
    box-shadow: inset 0 -5px 10px rgba(0, 0, 0, 0.1);
}

args.new_str:
.candle-item:hover {
    transform: translateY(-5px);
}

.candle-body {
    width: 20px;
    height: 80px;
    background: linear-gradient(to bottom, #f5f5f5, #e8e5e0, #d4af37);
    border-radius: 10px 10px 4px 4px;
    position: relative;
    box-shadow: inset 0 -5px 10px rgba(0, 0, 0, 0.1);
    transition: box-shadow var(--transition-normal);
}

.candle-lit .candle-body {
    box-shadow: 
        inset 0 -5px 10px rgba(0, 0, 0, 0.1),
        0 0 20px rgba(255, 193, 7, 0.3),
        0 0 40px rgba(255, 87, 34, 0.1);
}

args.old_str:
@media (max-width: 480px) {
    .hero-buttons {
        flex-direction: column;
        gap: var(--space-sm);
    }

    .btn {
        width: 100%;
        text-align: center;
    }

    .achievements-grid {
        grid-template-columns: 1fr;
    }

    .stats-container {
        grid-template-columns: repeat(2, 1fr);
