    }
}

args.new_str:
@media (max-width: 480px) {
    .hero-buttons {
        flex-direction: column;
        gap: var(--space-sm);
    }

    .btn {
        width: 100%;
        text-align: center;
    }

    .achievements-grid {
        grid-template-columns: 1fr;
    }

